<template>
  <section id="experience" class="experience-section">
      <div class="container">
          <div class="row">
              <h1>Experiencia</h1>
              <div class="timeline">
                <template v-for="event in experience">
                  <span class="node-year">{{event.year}}</span>
                  <div class="node"></div>
                </template>
              </div>
              <div v-for="event in experience" class="row event-div">
                <div class="col-sm-5 col-md-5" :class="{ 'col-md-offset-7': $index % 2 !== 0 }"> 
                  <div class="thumbnail thumbnail-event" :class="{ 'event-left': $index % 2 === 0, 'event-right': $index % 2 !== 0 }"> 
                    <img data-src="holder.js/100%x200" alt="100%x200" :src="event.image" data-holder-rendered="true" style="height: 200px; width: 100%; display: block;">
                    <div class="caption"> 
                      <h3>{{event.title}}</h3> 
                      <p>{{event.content}}</p> 
                    </div> 
                  </div> 
                </div>
              </div>
          </div>
      </div>
  </section>
</template>
<script>
export default {
  data () {
    return {
      experience: [
        {
          title: 'Titulo Test',
          year: '2012',
          image: 'data:image/svg+xml;base64,',
          content: 'Cras justo odio, dapibus ac facilisis in, egestas eget quam. Donec id elit non mi porta gravida at eget metus. Nullam id dolor id nibh ultricies vehicula ut id elit.'
        },
        {
          title: 'Titulo Test',
          year: '2013',
          image: 'data:image/svg+xml;base64,',
          content: 'Cras justo odio, dapibus ac facilisis in, egestas eget quam. Donec id elit non mi porta gravida at eget metus. Nullam id dolor id nibh ultricies vehicula ut id elit.'
        }
      ]
    }
  },
  ready () {
    let eventHeight = document.querySelector('.event-div').offsetHeight
    let nodes = document.querySelectorAll('.node')
    let years = document.querySelectorAll('.node-year')

    if (nodes) {
      for (let i = 0; i < nodes.length; i++) {
        nodes[i].style.top = (eventHeight * i) + 'px'
        years[i].style.top = (eventHeight * i) + 'px'
      }
      document.querySelector('.timeline').style.height = (eventHeight * nodes.length) + 'px'
    }
  }
}
</script>
